    int largestRectangleArea(int *heights,int n) {
        //int n=heights.size();

        vector<int> next(n);
        next=nextSmallerElement(heights,n);

        vector<int> prev(n);
        prev=prevSmallerElement(heights,n);

        int area=INT_MIN;
        for(int i=0;i<n;i++)
        {
            int l=heights[i];

            if(next[i]==-1)
                next[i]=n;
            int b=next[i]-prev[i]-1;
