    public int findTheArea(int[] h){
        int max = 0;
        Stack<Integer> stack = new Stack<>();
        stack.add(0);
        for(int i=1; i<h.length; i++){
            int curr = h[i];
            if(stack.isEmpty() || curr >= h[stack.peek()]){
                stack.add(i);
            } else {
                while(!stack.isEmpty() && curr < h[stack.peek()]){
                    int temp = h[stack.pop()];
