class Solution {
    vector<int> nextsmall(vector<int>& arr,int n){
        stack<int> s;
        s.push(-1);
        vector<int> ans(n);
        for(int i=n-1;i>=0;i--){
            int c=arr[i];
            while(s.top()!=-1&&arr[s.top()]>=c)
            {
                s.pop();
            }
            ans[i]=s.top();
            s.push(i);
        }
        return ans;
    }
    vector<int> prevsmall(vector<int>& arr,int n){
         stack<int> s;
