class Solution {
    public int maximalRectangle(char[][] matrix) {

        int row = matrix.length;
        int column = matrix[0].length;

        if (matrix == null || row == 0) return 0;

        int maxArea = Integer.MIN_VALUE;

        int []arr = new int[column];

        for (int i = 0; i < row; i++) {
            for (int j = 0; j < column; j++) {
                if (matrix[i][j] == '1') {
                    arr[j] = arr[j] + 1;
                } else {
                    arr[j] = 0;
                }
            }
            int currArea = getMaxArea(arr, column);
            maxArea = Math.max(maxArea, currArea);
        }
        return maxArea;
    }

    public int getMaxArea(int []arr, int len) {

        int pse[] = getPreviousSmall(arr, len);
        int nse[] = getNextSmall(arr, len);

        int maxArea = Integer.MIN_VALUE;

        for (int k = 0; k < len; k++) {
            int currArea = (nse[k] - pse[k] - 1) * arr[k];
            maxArea = Math.max(maxArea, currArea);
        }

        return maxArea;
    }

    public int[] getPreviousSmall(int []arr, int len) {

        Stack<Integer> s = new Stack<>();
        s.push(0);

        int pse[] = new int[len];
        pse[0] = -1;

        for (int i = 1; i < len; i++) {
            while(!s.isEmpty() && arr[s.peek()] >= arr[i]) {
                s.pop();
            }

            if (s.isEmpty()) {
                pse[i] = -1;
            } else {
                pse[i] = s.peek();
            }
            s.push(i);
        }
        return pse;
    }

    public int[] getNextSmall(int []arr, int len) {

        Stack<Integer> s = new Stack<>();
        s.push(len - 1);

        int nse[] = new int[len];
        nse[len - 1] = len;

        for (int j = len - 2; j >= 0; j--) {
            while(!s.isEmpty() && arr[s.peek()] >= arr[j]) {
                s.pop();
            }

            if (s.isEmpty()) {
                nse[j] = len;
            } else {
                nse[j] = s.peek();
            }
            s.push(j);
        }
        return nse;
    }
}
