        return res;
    }
    int maxInRow(vector<int> v,int n){

        int res=0;
        v.push_back(0);
        stack<int> st;

        int i=0;
        while(i<n+1){
            if(st.empty()|| v[i]>= v[st.top()]) st.push(i++);
            else{
                int index=st.top();
                st.pop();
                if(st.empty()) res=max(res, i*v[index]);
                else{
                    res=max(res,v[index]*(i-st.top()-1));
                }                
            }     
        } 
       return res;     
    }
};
