 int maxRectangleHistogram(vector<int>h,int n){
        stack<int>s;
        int res=0;
        for(int i=0;i<n;i++){
            while(s.empty()==false && h[s.top()]>=h[i]){
                int tp=s.top();
                s.pop();
                int curr=h[tp]*(s.empty()? i : (i-1-s.top()));
                res=max(curr,res);
            }
            s.push(i);
        }
        while(!s.empty()){
            int tp=s.top();
            s.pop();
            int curr=h[tp]*(s.empty()? n : (n-1-s.top()));
            res=max(curr,res);
        }
        return res;
    }


