class Solution {
private:
    int largestRectangleArea(vector<int>& heights) {
        stack<pair<int, int>> st;
        int n = heights.size();
        vector<int> nsl(n), nsr(n), area;
        for(int i=0; i<n; i++) {  // Nearest Smallest Element to Left
            while(!st.empty() && st.top().first >= heights[i]) st.pop();
            if(st.empty()) nsl[i] = -1;
            else nsl[i] = st.top().second;
            st.push({heights[i], i});
        }
        while(!st.empty()) st.pop();
        for(int i=n-1; i>=0; i--) { // Nearest Smallest Element to Right
            while(!st.empty() && st.top().first >= heights[i]) st.pop();
            if(st.empty()) nsr[i] = n;
            else nsr[i] = st.top().second;
            st.push({heights[i], i});
        }
        int ans = 0;
        for(int i=0; i<n; i++) {
            int width = nsr[i]-nsl[i]-1;
            area.push_back(heights[i]*width);
            ans = max(ans, area[i]);
        }
        return ans;
    }
