class Solution {
    int maxHistogramArea(vector<int>& heights,int n){

	// TC -> O(M*(N+M))
	// SC -> O(N)

    stack<int>stk;
    int maxArea{};

    for(int i=0;i<=n;i++){
        while(!stk.empty() && (i==n || (heights[stk.top()]>=heights[i]))){
            int height=heights[stk.top()];
            stk.pop();

            int width;
            if(stk.empty()) width=i;
            else width=i-stk.top()-1;

            maxArea=max(maxArea, height*width);
        }
        stk.push(i);
    }
    return maxArea;
}
