public:
    // copy pasting from 84. Largest Rectangle in Histogram
    int largestRectangleArea(vector<int>& heights) {
        vector<int>NSL(heights.size());
        stack<int>st;

        //for next smaller left(indexes)
        for(int i=0;i<heights.size();i++){
        while( !st.empty() && heights[i] <= heights[st.top()] ) st.pop();
            (st.empty()) ? NSL[i]=-1 : NSL[i]= st.top();
            st.push(i);
        }
        stack<int>t;
        st=t;
        int ans = 0;
        for(int i=heights.size()-1;i>=0;i--){
        while( !st.empty() && heights[i] <= heights[st.top()] ) st.pop();

            if(st.empty()) {
                int m = heights.size() - NSL[i] - 1;
            ans = max(ans , m*heights[i]);
            }

            else{
                int m = st.top()-NSL[i]-1;
                ans= max(ans,m*heights[i] );
            } 
            st.push(i);
