class Solution {
public:
    int maximalRectangle(vector<vector<char>>& matrix) {
        int row=matrix.size();
        if(row==0) return 0;
        int col=matrix[0].size();

        int area=0;

        vector<vector<int>> grid(row,vector<int>(col));

        for(int i=0;i<row;++i){
            for(int j=0;j<col;++j){
                if(i==0){
                    grid[i][j]=matrix[i][j]-'0';
                }else{
                    if(matrix[i][j]=='0'){
                        grid[i][j]=0;
                    }else{
                        grid[i][j]=grid[i-1][j]+1;
                    }
                }
            }

			//code below is similar to "MAX AREA IN HISTOGRAM" problem
            vector<int> left, right;
            stack<int> stk;

            for(int j=0;j<col;++j){
                int cur = grid[i][j];
                while(!stk.empty() && grid[i][stk.top()]>=cur) stk.pop();
                if(stk.empty()) left.push_back(-1);
                else left.push_back(stk.top());
                stk.push(j);
            }

            while(!stk.empty()) stk.pop();

            for(int j=col-1;j>=0;--j){
                int cur = grid[i][j];
                while(!stk.empty() && grid[i][stk.top()]>=cur) stk.pop();
                if(stk.empty()) right.push_back(col);
                else right.push_back(stk.top());
                stk.push(j);
            }
            reverse(right.begin(),right.end());

            for(int j=0;j<col;++j){
                area=max(area, (grid[i][j]*(right[j]-left[j]-1)));
            }
        }

        return area;
    }
};
