Most of solution in discuss is DP, in here I use `prefix sum + binary search`, of course it' slow...but maybe new way for your. Hope it's helpful for you.
```c++
class Solution {
public:
    int maximalRectangle(vector<vector<char>>& matrix) {
        if(matrix.empty()||matrix[0].empty()) return 0;
		int n=matrix.size(),m =matrix[0].size();
        printf("%d,%d\n",n,m);
		vector<vector<int>> sum(n+1,vector<int>(m+1,0));
		for(int i=1;i<=n;i++) for(int j=1;j<=m;j++){
			int tmp = matrix[i-1][j-1]=='1'?1:0;
		    // prefix sum
			sum[i][j] = sum[i-1][j] + sum[i][j-1] - sum[i-1][j-1] + tmp;
		}

		int ans=0,cnt=0;
        for(int i=n;i>=1;i--){
            if(ans>=i*m) break;
            for(int j=m;j>=1;j--){
                if(ans>=i*j) break;
                if(matrix[i-1][j-1]!='1') continue;
                for(int d=min(m,j);d>=1;d--){
                    int l=0, r= n,mid,t=0;
                    // binary search
                    while(l<=r){
                        mid = l + (r-l)/2;
                        if(i-mid<0||j-d<0||sum[i][j]-sum[i-mid][j]-sum[i][j-d]+sum[i-mid][j-d]<d*mid) r=mid-1;
                        else l = mid+1;
                    }
                    ans = max(ans,r*d);
                }
            }
        }
        return ans;
    }
};
