 int maximalRectangle(vector<vector<char> > &matrix) {
    if(matrix.empty()) return 0;

    // Since r and c dont change we use a constant sized array instead of vector
    const int r = matrix.size();
    const int c = matrix[0].size();

    /* height[j] stores the height of the possible rectangle at col=j for any row i
       left[j] stores the index of the col havin the leftMost edge of the rect at col=j for any row i
       right[j] stores the index of the col havin the rightMost edge of the rect at col=j for any row i
       Rect Area = (right[j]-left[j])*height[j]
    */

    // We find the largest rectangle for every triplet values in the order of rows:
     /*          0      1      2     3      4
     Index 0: (0 1 1)(0 0 5)(2 1 3)(0 0 5)(0 0 5)
     Index 1: (0 2 1)(0 0 5)(2 2 3)(2 1 5)(2 1 5)
     Index 2: (0 3 1)(0 1 5)(2 3 3)(2 2 5)(2 2 5)
     Index 3: (0 4 1)(0 0 5)(0 0 5)(3 3 4)(0 0 5)
     */
     // Note: At any index (i,j), triplet = (left, height, right)

    int left[c], right[c], height[c];

    // filling the arrays with default values
    fill_n(left,c,0);
    fill_n(right,c,c); // default value is not zero
    fill_n(height,c,0);

    int ans = 0;
    for(int i=0; i<r; i++) {

        int cur_left=0, cur_right=c; 

        /*
        height of rect at current (row,col) = height of rect at prev row on the same col + 1
        height[i][j] = height[i-1][j] + 1, if matrix[i][j] = 1
                     = 0, otherwise
        */
        for(int j=0; j<c; j++) {
            if(matrix[i][j]=='1') height[j]++; 
            else height[j]=0;
        }


        /*
        leftEdge of current Row = max(leftEdge of prev row, currentRowLeftEdge)
        leftEdge[i][j] = max(leftEdge[i-1][j], currenLeftEdge), if matrix[i][j] = 1;
                       = 0, otherwise, Also update the currentLeftEdge to next index because current matrix[i][j] is 0.
        */
        for(int j=0; j<c; j++) {
            if(matrix[i][j]=='1') left[j]=max(left[j],cur_left);
            else {
                left[j]=0;
                cur_left=j+1;
            }
        }

        /*
        RightEdge of current Row = min(rightEdge of prev row, currentRowRightEdge)
        rightEdge[i][j] = min(rightEdge[i-1][j], currentRowRightEdge), if matrix[i][j] = 1;
                       = col, otherwise. Also update the currentRightEdge to current index because current matrix[i][j] is 0.
        */
        for(int j=c-1; j>=0; j--) {
            if(matrix[i][j]=='1') right[j]=min(right[j],cur_right);
            else {
                right[j]=c;
                cur_right=j;
            }    
        }

        // For Debug Purpose:
        // cout<<"Index: "<<i<<" ";
        // for(int j = 0; j<c; j++) {
        //     cout<<"("<<left[j]<<" "<<height[j]<<" "<<right[j]<<")";
        // }
        // cout<<"\n";

        // compute the area of rectangle using (left, height, right), Area = (right-left)*height
        for(int j=0; j<c; j++) {
            ans = max(ans,(right[j]-left[j])*height[j]);
        }
    }
    return ans;
}
