class Solution {
public:
    int left[201][201], right[201][201], height[201][201];
    int maximalRectangle(vector<vector<char>>& matrix) {
        int n = matrix.size();
        if(n == 0)return 0;
        int m = matrix[0].size();
        if(m == 0)return 0;
        for(int i = 0; i < n; ++i){
            int l = -1;
            for(int j = 0; j < m; ++j){
                if(matrix[i][j] == '0'){
                    left[i][j] = j;
                    l = j;
                }else{
                    left[i][j] = l;
                }
            }
            int r = m;
            for(int j = m - 1; j >= 0; --j){
                if(matrix[i][j] == '0'){
                    right[i][j] = j;
                    r = j;
                }else right[i][j] = r;
            }
        }
        int ans = 0;
        for(int i = 0; i < m; ++i){
            if(matrix[0][i] == '1'){
                height[0][i] = 1;
            }
            ans = max(ans, height[0][i] * (right[0][i] - left[0][i] - 1));
        }
        for(int i = 1; i < n; ++i){
            for(int j = 0; j < m; ++j){
                if(matrix[i][j] == '1'){
                    height[i][j] = height[i - 1][j] + 1;
                    if(height[i - 1][j] != 0){
                        left[i][j] = max(left[i][j], left[i - 1][j]);
                        right[i][j] = min(right[i][j], right[i - 1][j]);
                    } 
                    ans = max(ans, height[i][j] * (right[i][j] - left[i][j] - 1));
                }

            }
        }
        return ans;
    }
};
