class Solution {
    public int maximalRectangle(char[][] matrix) {

        if(matrix ==null || matrix.length==0)
            return 0;

        int[] dp = new int[matrix[0].length];
        int res = 0;
        for(int i=0;i<matrix.length;i++) {
            for(int j=0;j<matrix[0].length;j++) {
                if(matrix[i][j] == '0') {
                    dp[j] = 0;
                    continue;
                }
                dp[j] = dp[j] + matrix[i][j]-'0';
            }
            res = Math.max(res, histogra(dp));
        }
        return res;
    }

    private int histogra(int[] dp) {

        Stack<Integer> st = new Stack<>();
        int res = 0;
        for(int i=0;i<dp.length;i++) {

            while(!st.isEmpty() && dp[i] <= dp[st.peek()] ) {

                int top = st.pop();
                int area = (i-(st.isEmpty() ? -1 : st.peek())-1)*dp[top];
                res = Math.max(area, res);
            }   
            st.push(i);
        }
        while(!st.isEmpty()) {
            int top = st.pop();
            int area = (dp.length-(st.isEmpty() ? -1 : st.peek())-1)*dp[top];
            res = Math.max(area, res);
        }
        return res;
    }
}
