// note that there are 2 published O(M) space solutions 
class Solution {
public:
    int maximalRectangle(vector<vector<char>>& matrix) 
    {
        if (matrix.empty ())
            return 0;

        vector <vector <pair <int,int>>> dp (matrix.size ()+1, vector <pair <int,int>> (matrix [0].size ()+1)); // largest rectangle with (i-1, j-1) as bottom right vertex.
        vector <vector <int>> dpr (matrix.size ()+1, vector <int> (matrix [0].size ()+1)); // longest horizontal line with (i-1, j-1) as right end point.
        vector <vector <int>> dpc = dpr; // tallent vertical line with (i-1, j-1) as bottom end point.
        int ret = 0;

        for (int i = 0; i < matrix.size (); i++)
            for (int j = 0; j < matrix [0].size (); j++)
                if (matrix [i][j] == '1')
                {
                    // build out horizontal, vertical lines.
                    dpr [i+1][j+1] = 1 + dpr [i+1][j];
                    dpc [i+1][j+1] = 1 + dpc [i][j+1];

                    // build out rectangle.
                    // 1. based on contained rectangle with current row, column taken out.
                    dp [i+1][j+1] = {1 + min (dp [i][j].first, dpc [i][j+1]), 1 + min (dp [i][j].second, dpr [i+1][j])};

                    // 2. based on horizontal line.
                    if (dpr [i+1][j+1] > dp [i+1][j+1].first * dp [i+1][j+1].second)
                        dp [i+1][j+1] = {1, dpr [i+1][j+1]};

                    // 3. based on veritical line.
                    if (dpc [i+1][j+1] > dp [i+1][j+1].first * dp [i+1][j+1].second)
                        dp [i+1][j+1] = {dpc [i+1][j+1], 1};

                    // maintain return value.
                    ret = max (ret, dp [i+1][j+1].first * dp [i+1][j+1].second);
                }

        return ret;
    }
};
