class Solution(object):
    def maxArea(self, bar):
        if not bar:
            return 0
        bar.append(0)
        s, ret = [], 0
        for i in range(len(bar)):
            while s and bar[i] <= bar[s[-1]]:
                h = bar[s[-1]]
                s.pop()
                if s:
                    w = i -1 - s[-1]
                else:
                    w = i
                ret = max(ret, h*w)
            s.append(i)
        return ret
    def maximalRectangle(self, matrix):
        """
        :type matrix: List[List[str]]
        :rtype: int
        """
        if not matrix:
            return 0
        M, N = len(matrix), len(matrix[0])
        bar = [0]*N
        ret = 0
        for i in range(M):
            for j in range(N):
                if matrix[i][j] == '1':
                    bar[j]+=1
                else:
                    bar[j] = 0
            ret = max(ret, self.maxArea(bar))
        return ret
