            if(matrix[i][0]=='1'){
                up[i][0]=up[i-1][0]+1;
                left[i][0]=1;
                max=Math.max(up[i][0],max);
            }
        }

        for(int j=1;j<col;j++)
        {
            if(matrix[0][j]=='1'){
                left[0][j]=left[0][j-1]+1;
                up[0][j]=1;
                max=Math.max(left[0][j],max);
            }
        }
        for(int i=1;i<row;i++)
        {
            for(int j=1;j<col;j++)
            {
                if(matrix[i][j]=='1')
                {
                    int upto=0;
                    left[i][j]=left[i][j-1]+1;
                    up[i][j]=up[i-1][j]+1;

                    if(left[i][j]>1 && up[i][j]>1)
                    {
                        int val=0; 
                        int track=i;
                        int min=left[i][j];
                        while(track>=0 && left[track][j]>1 && up[track][j]>=1)//this is important when both top and left are
                        {                                                     //non zero
                            if(min>left[track][j])
                                min=left[track][j];
                            val++;
                            upto=Math.max(min*val,upto);
                            track--;
                        }
                    }

                    max=Math.max(max,Math.max(left[i][j],Math.max(up[i][j],upto)));
                }
                else
                {
                    left[i][j]=0;
                    up[i][j]=0;
                }
            }
        }
        return max;
    }
