// Induction Rule: dp[i][j] represents the cur largest length at row i, from 0 to j
// Base case: j == 0 && matrix[i][j] == 1  --> length == 1
// Time: O(rows * cols * rows) --> O(rows * rows * cols) = O(N^2 * M)
// Space: 2d array : O(rows * cols) -> O(N * M)
class Solution {
	public int maximalRectangle(char[][] matrix) {
		if (matrix == null || matrix.length == 0) {
			return 0;
		}
		int maxArea = 0;
		int rows = matrix.length;
		int cols = matrix[0].length;
		int[][] dp = new int[rows][cols];
		for (int i = 0; i < rows; i++) {
			for (int j = 0; j < cols; j++) {
				if (matrix[i][j] == '1') {
					dp[i][j] = j == 0 ? 1 : dp[i][j - 1] + 1; 
					int length = dp[i][j];
					for (int k = i; k >= 0; k--) {
						length = Math.min(length, dp[k][j]);  // horizontal 
						int width = i - k + 1; // vertical
						maxArea = Math.max(maxArea, length * width);
					}
				}
			}
		} 
		return maxArea;
	}
}

