    public class Solution
    {
        //https://leetcode.com/problems/largest-rectangle-in-histogram/
        private int LargestRectangleArea(int[] heights)
        {
            if (heights.Length == 0)
            {
                return 0;
            }

            if (heights.Length == 1)
            {
                return heights[0];
            }

            int res = heights[0];
            Stack<int> indices = new Stack<int>();
            for (int i = 0; i < heights.Length; i++)
            {
                while (indices.Count > 0 && heights[indices.Peek()] >= heights[i])
                {
                    var pop = indices.Pop();
                    var leftMost = indices.Count > 0 ? indices.Peek() + 1 : 0;
                    res = Math.Max(res, heights[pop] * (i - leftMost));
                }
                indices.Push(i);
            }

            while (indices.Count > 0)
            {
                var pop = indices.Pop();
                var leftMost = indices.Count > 0 ? indices.Peek() + 1 : 0;
                res = Math.Max(res, heights[pop] * (heights.Length - leftMost));
            }
            return res;
        }

        public int MaximalRectangle(char[][] matrix)
        {
            if (matrix.Length == 0)
            {
                return 0;
            }

            int res = 0;

            int[] heights = null;
            for (int i = 0; i < matrix.Length; i++)
            {
                if (heights == null)
                {
                    heights = matrix[i].Select(c => c - '0').ToArray();
                }
                else
                {
                    for (int j = 0; j < matrix[i].Length; j++)
                    {
                        if (matrix[i][j] == '0')
                        {
                            heights[j] = 0;
                        }
                        else
                        {
                            heights[j]++;
                        }
                    }
                }

                res = Math.Max(res, LargestRectangleArea(heights));
            }

            return res;
        }
    }
