class Solution:
    def maximalRectangle(self, matrix: List[List[str]]) -> int:
        mat = matrix
        if mat==[]:
            return 0
        else:
            def histogram_area(arr):
                stack = []
                max_area,index = 0,0
                while index<len(arr):
                    if (not stack) or (arr[stack[-1]])<=arr[index]:
                        stack.append(index)
                        index+=1
                    else:
                        top = stack.pop()
                        if stack:
                            area = arr[top]*(index-stack[-1]-1)
                        else:
                            area = arr[top]*index
                        max_area = max(area,max_area)
                while stack:
                    top = stack.pop()
                    if stack:
                        area = arr[top]*(index-stack[-1]-1)
                    else:
                        area = arr[top]*index
                    max_area = max(area,max_area)
                return max_area
            for i in range(len(mat)):
                if i==0:
                    arr = []
                    for j in mat[i]:
                        if j=='0':
                            arr.append(0)
                        else:
                            arr.append(1)
                    max_area = histogram_area(arr)
                else:
                    temp = []
                    for j in range(len(mat[i])):
                        if mat[i][j] == '0':
                            temp.append(0)
                        else:
                            temp.append(arr[j]+1)
                    area = histogram_area(temp)
                    max_area = max(area,max_area)
                    arr = temp
            return max_area





