        int count=0,ans=0,val;
        for(int i=0;i<m;i++)
        {
          for(int j=0;j<n;j++)
          {
            if(arr[j]!=0 and matrix[i][j]=='0'){
              arr[j]=0;
            }
            else if(matrix[i][j]=='1'){
              arr[j]+=1;
            }
          }
          for(int k=0;k<n;k++)
          {
            if(arr[k]!=0){
              if(count==0){
                count++;
                val=arr[k];
              }
              else{
                count++;
                val=min(val,arr[k]);
              }
              ans=max(ans,count*val);
            }
            else{
              count=0;
            }
          }
          count=0;
        }
        return ans;
    }
};


