```
class Solution {
public:
    bool buddyStrings(string s, string goal) {
        // Get the lengths of the input strings
        int len = s.size();
        int glen = goal.size();

        // If the lengths are different, return false
        if (len != glen)
            return 0;

        // Initialize a variable to keep track of the number of differences between s and goal
        int diff = 0;

        // Initialize a frequency array to count the occurrences of each character in s
        vector<int> freq(26, 0);

        // Initialize a flag to check if s has any duplicate characters
        bool hasDuplicates = 0;

        // Initialize a vector to store the indices where characters need to be swapped
        vector<int> needSwap;

        // Iterate over the characters of s and goal
        for(int i = 0; i < len; i++){
            // If the characters at the current position are different, increment the difference count
            // and add the index to the needSwap vector
            if (s[i] != goal[i]){
                diff++;
                needSwap.push_back(i);
            }

            // Increment the frequency of the current character in s
            freq[s[i]-'a']++;

            // If there are no duplicates yet and the frequency of the current character is 2 or more,
            // set the hasDuplicates flag to true
            if (!hasDuplicates && freq[s[i]-'a'] >= 2) 
                hasDuplicates = 1;
        }

        // If s is equal to goal and it has duplicates, return true
        if (s == goal && hasDuplicates)
            return 1;

        // If s is not equal to goal and there are exactly 2 differences, try swapping the characters
        if (s != goal && diff == 2){
            // Swap the characters at the indices stored in needSwap
            swap(s[needSwap[0]], s[needSwap[1]]);

            // If the resulting string is equal to goal, return true
            if (s == goal)
                return 1;
            else
                return 0;
        }

        // If none of the above conditions are satisfied, return false
        return 0;
    }
};

```
