class Solution {
public:
  bool buddyStrings(string A, string B) {        
        if (A == B)
            return (set<char>(A.begin(), A.end()).size() < A.size()); // swap same letters in A

        int n = A.length();
        int l = 0, r = n-1;

        while(l < n && A[l] == B[l])
            l++;
        while(r >= 0 && A[r] == B[r])
            r--;
        if(l < r)
            swap(A[l], A[r]);

        return A == B;                
    }
};
