class Solution {
    public boolean buddyStrings(String A, String B) {
        //case 1: if the lengths are not equal, return false
        if(A.length()!=B.length())
            return false;
        //case 2: if strings are equal we have to make sure that 
        // atleast there are two instances of any char.
        // for example 'a' 'a' returns false but 'aa' 'aa' returns true
        if(A.equals(B)) {
            Set<Character> set = new HashSet<>();
            for(char c : A.toCharArray()) {
                if(set.contains(c))
                    return true;
                set.add(c);   
            }
            return false;
        }
        //case 3: check if the strings only differ at only two indexes and
        //chars at those diff indexes are equal.\
        // for example: aabc , aacb returns true.
        // aabc, aace -> return false.
        List<Integer> diff = new ArrayList<>();
        for(int i=0;i<A.length();i++) {
            if(A.charAt(i)!=B.charAt(i))
                diff.add(i);
        }
        return diff.size()==2 && (A.charAt(diff.get(0)) == B.charAt(diff.get(1))  &&
                                  B.charAt(diff.get(0)) == A.charAt(diff.get(1)));
    }
}
