class Solution {
public:
    bool buddyStrings(string s, string goal) {
        if(s.size()!=goal.size()) return false;
        if(s==goal){
            unordered_map<char,int>m;
            for(auto a:s){
                m[a]++;
                if(m[a]>1)return true;
            }
            return false;
