```
class Solution {
public:
    bool buddyStrings(string s, string goal) {
        // Check if the sizes of the strings are different or if the size is 1
        // In such cases, it's not possible to swap characters to make the strings equal
        if (s.size() != goal.size() || s.size() == 1)
            return false;

        vector<int> different; // Store the indices where the characters are different
        vector<int> ct(26, 0); // Store the count of each character in string s
        bool f = false; // Flag to check if there are duplicate characters in string s

        for (int i = 0; i < s.size(); i++) {
            if (s[i] != goal[i])
                different.push_back(i); // Add index to the different vector
            else {
                ct[s[i] - 'a']++; // Increment the count of character s[i]
                if (ct[s[i] - 'a'] >= 2)
                    f = true; // Set flag to true if character s[i] is repeated
            }
        }

        if (different.size() > 2)
            return false; // If there are more than 2 different characters, swapping is not possible

        if (different.size() == 1)
            return false; // If there is only 1 different character, swapping is not possible

        if (different.size() == 0)
            return f; // If there are no different characters, return whether there are duplicate characters

        // There are exactly 2 different characters, so swap them in string s
        int in1, in2;
        in1 = different[0];
        in2 = different[1];
        swap(s[in1], s[in2]);

        // Check if the swapped string is equal to the goal string
        return s == goal;
    }
};

```
