class Solution {
    public boolean buddyStrings(String A, String B) {
        //Base case
        if(A.length() != B.length()){
            return false;
        }
        //Two Cases :
        //Case 1: if both strings are same
        if(A.equals(B)){
            //get character count
            int[] count = new int[26];
            for(char c : A.toCharArray()){
                count[c - 'a']++;
            }
            //check if there are characters of frequency greater than 1
            //Eg : A = aaaabc , B = aaaabc
            //since freq(a) > 1 , we can swap two a's
            for(int freq : count){
                if(freq > 1){
                    return true;
                }
            }
            //no character has frequency greater than 1
            //Eg : A = abcd , B = abcd
            return false;
        }
        //Case 2 : if A and B are not equal
        else{
            //get pos of elements to swap
            int first = -1;
            int second = -1;
            for(int i=0;i<A.length();i++){
                if(A.charAt(i) != B.charAt(i)){
                    //check if we filled first index
                    if(first == -1){
                        first = i;
                    }
                    //check if we filled first index
                    else if(second == -1){
                        second = i;
                    }
                    //both first and second are filled and there exists a third diff
                    else{
                        return false;
                    }
                }
            }
            //check if second pos is not filled
            if(second == -1){
                return false;
            }
            //check id characters at pos are same
            return (A.charAt(first) == B.charAt(second)) && (A.charAt(second) == B.charAt(first));
        }

    }
}
