class Solution {
    fun buddyStrings(s: String, goal: String): Boolean {
        if (s.length != goal.length || s.length < 2) {
            return false
        }

        if (s == goal) {
            val charSet = s.toSet()
            return charSet.size < s.length
        }

        val diffIndices = IntArray(2)
        var diffCount = 0

        for (i in s.indices) {
            if (s[i] != goal[i]) {
                if (diffCount >= 2) {
                    return false
                }
                diffIndices[diffCount] = i
                diffCount++
            }
        }

        if (diffCount != 2) {
            return false
        }

        val (index1, index2) = diffIndices

        return s[index1] == goal[index2] && s[index2] == goal[index1]
    }
}

```

