class Solution {
public:
    bool buddyStrings(string s, string goal) {
        if(s.size()!=goal.size()) return false;
        int diff=0;
        vector<int> freq1(26,0);
        vector<int> freq2(26,0);

        for(int i=0;i<s.size();i++){
            char ch1= s[i];
            char ch2= goal[i];
            freq1[ch1-'a']++;
            freq2[ch2-'a']++;
        }

        bool cnt=false;
        for(int i=0;i<26;i++){
            if(freq1[i]!=freq2[i]) return false;
            if(freq1[i]>=2) cnt=true;
        }

        for(int i=0;i<s.size();i++){
            if(s[i]!=goal[i]) diff++;
        }
        return (diff==2  ||  (cnt==true && diff==0));
    }
};
