class Solution {
public: 
    bool buddyStrings(string s, string goal) {

        //Creating Hashmaps
        unordered_map<char, int> smp; 
        unordered_map<char, int>  gmp;  

        //Initializing each key with frequencies
        for(char c : s) { 
            smp[c]++;
        }     

        for (char c : goal) { 
            gmp[c]++;
        } 

        //If they don't have same chars and frequencies return false
        if (smp != gmp) {   
            return false;
        } 

        //If strings are equal and has a duplicate char then return true
        if (s == goal) { 
            for (auto c : smp) { 
                if (c.second >= 2) {  
                    return true;
                }
            }    

            //if all chars are unique return false
            return false;
        }

        //Two pointerapproach we have to just swap two chars not more than 2
        int i = 0; 
        int j = 0; 
        int ctr = 0;

        while (i < s.length()) { 
            if (ctr > 2) { 
                return false;
            } 

            if (s[i] != goal[j]) { 
                ctr++;
            } 

            i++; 
            j++;
        } 

        return (ctr > 2) ? false : true; 
   }
};
