    def can_be_replaced(self, s1, s2):        
        diff_chars = []                        
        for i in range(len(s1)):
            if s1[i] != s2[i]:
                diff_chars.append((s1[i], s2[i]))

        if len(diff_chars) != 2: return False

        return diff_chars[0][0] == diff_chars[1][1] and diff_chars[0][1] == diff_chars[1][0]
