        #return diff_chars[0] == diff_chars[1][::-1]

    def buddyStrings(self, s: str, goal: str) -> bool:
        if len(s) != len(goal): return False

        if s == goal:  return max(Counter(s).values()) > 1        

        return self.can_be_replaced(s, goal)
