```swift
func buddyStrings(_ A: String, _ B: String) -> Bool {
	guard A.count == B.count else { return false }

	// 1. Strings are equal. Is there a duplicate we can swap? 
	if A == B {
		var set = Set<Character>() 
		for char in A {
			if set.contains(char)  {
				return true 
			}
			set.insert(char)
		}
		return false 
	}

	// 2. Strings are different. Ensure swapping maintains equality.
	var pairs = [[Character]]()
	for (a, b) in zip(A, B) {
		if a != b {
			pairs.append([a, b])
		}
		if pairs.count > 2 {
			return false 
		}
	}

	return pairs.count == 2 && pairs[0] == pairs[1].reversed()
}
```

