class Solution {
    public boolean buddyStrings(String A, String B) {
        if (A.length() != B.length())   return false;

        if (A.equals(B)) {
            Set<Character> set = new HashSet();
            for (char ch : A.toCharArray()) set.add(ch);
            return set.size() < A.length();
        }

