            if (A.charAt(i) != B.charAt(i)) {
                diff.add(i);
                if (diff.size() > 2)    return false;
            }
        }

        return diff.size() == 2 && 
            A.charAt(diff.get(0)) == B.charAt(diff.get(1)) && 
            A.charAt(diff.get(1)) == B.charAt(diff.get(0));
    }
}
