class Solution {
public:
bool checkDuplicate(string &s)
{
    int CountOfEachCharacter[26] = {0}; // let count of all element is 0
    for(auto &it : s)
    {
        CountOfEachCharacter[it-'a']++; // Add the count of each character
        if(CountOfEachCharacter[it-'a'] > 1) // If any element have more than one count, it implies we have a duplicate
            return true;
    }
    return false; // No duplicate found
}
    bool buddyStrings(string s, string goal) {
        if(s.length() != goal.length()) return false; // If the length of both strings are not same , then fasle;

        if(s == goal){  // if both strings are identical i.e abab and abab
            return checkDuplicate(s);
