        vector<int> index; // To contain the index of elements to be swapped 
        int count = 0;
        for(int i = 0 ; i < s.length() ; i++)
        {
            if(s[i] != goal[i])
                index.push_back(i),count++;  // Store the index of element where ele are not same like s= abc and goal= acb
