class Solution {
public:
    bool buddyStrings(string s, string goal) {
        if(s==goal){
            set<char>st;
            for(auto ele:s){
                st.insert(ele);
            }
            return (st.size()!=s.size());
        }
        if(s.size()!=goal.size()){
            return false;
        }
        int cnt=0;
        for(int i=0;i<s.size();i++){
            cnt+=(s[i]!=goal[i]);
        }
        if(cnt!=2){
            return false;
        }
        int ind1=-1,ind2=-1;
        for(int i=0;i<s.size();i++){
            if(s[i]!=goal[i]){
                ind1=i;
                break;
            }
        }
        for(int i=ind1+1;i<s.size();i++){
            if(s[i]!=goal[i]){
                ind2=i;
                break;
            }
        }
        swap(s[ind1],s[ind2]);
        return s==goal;
    }
};
```

