class Solution {
public:
    int binaryGap(int N) 
    {
        constexpr const size_t Byte_Size = 8;
        std::bitset<sizeof(N) * Byte_Size> bits{ N };

        size_t i = 0;
        while(!bits.test(i)) // find first set bit
            ++i; 

        auto max_dist = 0;
        auto current_dist = 0;
        for(++i; i < bits.size(); ++i)
        {
            ++current_dist;
            if(bits.test(i))
            {
                max_dist = std::max(max_dist, current_dist);
                current_dist = 0;
            }
        }

        return max_dist;
    }
};
```

