class Solution {
public:
    int binaryGap(int N) {
        int ans = 0;
        if(N &&  !(N & (N-1))) return 0; //power of 2
        bool first_one = false;
        int k = 0;
        int one_idx;
        while(N) {
            k++;
            if(first_one) {
                if(N&1 == 1) {
                    ans = max(ans, k - one_idx);
                }
            }
            if(N&1 == 1) {
                first_one=true;
                one_idx = k;
            }
            N >>= 1;
        }
        return ans;
    }
};
```

