func binaryGap(N int) int {
    maxGap := 0

    currentGap := 0
    for N > 0 {

        //Check if right most bit is a one
        if (N & 1 == 1) {
            if(currentGap > maxGap){
                maxGap = currentGap
            }
            currentGap = 1

            //need this to make sure we don't increment currentGap until we have actualy hit our first one
        } else if (currentGap > 0){
            currentGap++
        }
        //right shift by 1
        N = N >> 1
    }

    return maxGap
}
