class Solution {
public:
    int binaryGap(int N) {
        int last = -1;
        int max = 0;
        // Run through each bit 
        for (int i = 0; i < 32; i++) {
            // If the bit is a 1
            if (N & (1 << i))  {
                // If this isn't the first bit, check to see if the
                // this bit defines the new max distance
                if (last != -1) max = std::max(max, i - last);
                // Change this bit to last
                last = i;
            }
        }
        return max;
    }
};
