public class Solution
{
    public int BinaryGap(int N)
    {
        if ((N & (N - 1)) == 0) // Checks for N == 0 and N being a power of two
            return 0;
        // If the preceding condition was false then N has at leat two 1s
        while ((N & 1) == 0) // Find the first 1
            N >>= 1;
        N >>= 1; // Since we're intrested in the gap between 1s the first 1 is skipped
				//(otherwise it would cause an additional meaningless iteration of the following loop)
        int maxGap = 0;
        int gap = 0;
        while (N != 0)
        {
            if ((N & 1) == 0) // If it's 0 then increase the current gap
                gap++;
            else // If it's 1
            {
                if (gap > maxGap) // If current gap is bigger than the max one we change the max
                    maxGap = gap;
                gap = 0; // Reset the counter
            }
            N >>= 1;
        }
        return maxGap + 1; // Because maxGap holds the number of 0s between two 1s we increment it to represent the gap
    }
}
