class Solution {

    constexpr inline int trailing_zero(int n) {

        constexpr int table[] = {32, 0, 1,
            26, 2, 23, 27, 0, 3, 16, 24, 30, 28, 11,
            0, 13, 4, 7, 17, 0, 25, 22, 31, 15, 29,
            10, 12, 6, 0, 21, 14, 9, 5, 20, 8, 19,
            18};


        return table[(-n & n) % 37];        
    }

public:
    int binaryGap(int N) {
        int m = 0;

        int last = trailing_zero(N);
        while(N) {
            int t = trailing_zero(N);  // computes the number of zeros to the right of the rightmost one

            m = max(m, t - last);
            last = t;

            N -= (1 << t);  // Zeroes out the rightmost one
        }

        return m;
    }
};
