class Solution {
public:
    int binaryGap(int n) {
        auto max_distance = 0;
        while (!(n & 1)) n >>= 1;
        auto distance = 0;
        while (n != 0) {
            const bool increase = (max_distance - distance) & (1 << 31);
            max_distance = increase * distance + (!increase) * max_distance;
            distance = !(n & 1) * distance + 1;
            n >>= 1;
        }
        return max_distance;
    }
};
```

