function binaryGap(n: number): number {
    const bin = n.toString(2);
    const timeAppear: number = `${bin}`.match(/1/g)?.length || 0;
    if(timeAppear < 2) return 0;
    let maxGap: number = 0;
    let gap: number = 0;
    const binArr = bin.replace(/^0+|0+$/g, "").split('');
    for(let num of binArr){
        gap++;
        if(gap > maxGap) maxGap = gap;
        if(num == '1') gap = 0;
    }
    return maxGap;
};
