* ***Using Hashmap***

* ***Time Complexity :- O(Constant * logN)***

* ***Space Complexity :- O(Constant)***

```
class Solution {
public:

    // function for checking is two numbers have identiacal digits

    bool is_possible(int num1, int num2)
    {
        vector<int> mp(10, 0);

        // increment the count of digits by num1 

        while(num1)
        {
            int r = num1 % 10;

            mp[r]++;

            num1 = num1 / 10;
        }

        // decrement the count of digits by num2

        while(num2)
        {
            int r = num2 % 10;

            mp[r]--;

            num2 = num2 / 10;
        }

        // check if both are idenical of not

        for(int i = 0; i < 10; i++)
        {
            if(mp[i] != 0)
                return false;
        }

        return true;
    }

    bool reorderedPowerOf2(int n) {

        // first of all find all the power of 2 <= 1e9 and store in power array

        int num = 1;

        vector<int> power;

        power.push_back(1);

        while(num * 2 <= 1e9)
        {
            power.push_back(num * 2);

            num = num * 2;
        }

        // compare the number with every element of power array, if any of them have the identical digits 

        // then we can reorder the number to power of 2

        for(int i = 0; i < power.size(); i++)
        {
            if(is_possible(power[i], n))
                return true;
        }

        return false;
    }
};
```

