class Solution {
public:
    int comp(int a,int b)
    {
        bool A[10]={0},B[10]={0};
        int lena=0,lenb=0;
        while(a>0 || b>0)
        {
            if(a>0)
            {
                A[a%10]=1;
                a/=10;
                lena++;
            }
            if(b>0)
            {
                B[b%10]=1;
                b/=10;
                lenb++;
            }
        }

        if(lena!=lenb)
            return false;

        for(int i=0 ;i<10 ;i++)
            if(A[i]!=B[i])
                return false;
        return true;
    }

    bool reorderedPowerOf2(int N) {
        int k=1;
        vector<int> v;
        for(int i=0 ;i<30 ;i++)
        {
            v.push_back(k);
            k*=2;
        }

        for(int i=0 ;i<=30 ;i++)
        {
            if(comp(N,v[i]))
                return true;
        }
        return false;
    }
};
