class Solution {
    public boolean reorderedPowerOf2(int n) {
        int digitCount[] = getDigitCount(n);
        int totalDigits = 0;

        for (int count : digitCount) {
            totalDigits += count;
        }

		// set upper and lower limit
        int max = (int) Math.pow(10, totalDigits);
        int min = (int) Math.pow(10, totalDigits - 1);
        int powTwo = 1;

		// shift until within upper and lower limit range
        while (powTwo < min) {
            powTwo <<= 1;
        }

        boolean same = true;
        while (powTwo < max) {
            int[] powTwoDigitCount = getDigitCount(powTwo);
            same = true;

			// check if digits occurrences are equal
            for (int i = 0; i < digitCount.length; i++) {
                if (digitCount[i] != powTwoDigitCount[i]) {
                    same = false;
                    break;
                }
            }

            if (same) break;
            powTwo <<= 1;
        }

        return same;
    }

    private int[] getDigitCount(int n) {
        int digitCount[] = new int[10];
        while (n != 0) {
            digitCount[n % 10]++;
            n /= 10;
        }

        return digitCount;
    }
}
