var reorderedPowerOf2 = function(n) {
    const max = 10**9
    let power = 0;
    let pvalue = 0 
    const input = `${n}`.split('').sort((a, b) =>a -b).join('')
    while(pvalue <= max ) {
        pvalue = 2**power
        power++
        const p = `${pvalue}`.split('').sort((a, b) =>a -b).join('')
        if(p === input) { return true }  
        if(p.length > input.length) { return false }
    }
    return false
};

